import React, { useState } from 'react';
import { Box, TextField, Typography , Button} from '@mui/material';


const Login = () =>{
const [isSignup, setIsSignup] = useState(false);
console.log(isSignup);
    return (
        <div>
            <form>
                <Box display="flex" flexDirection={"column"} 
                width={350} 
                alignItems='center' 
                justifyContent={'center'}
                margin='auto'
                padding={5}
                marginTop={2}
                marginBottom={2}

                sx={{":hover":{
                    boxShadow:'10px 10px 20px #ccc',
                    borderRadius:5,
                },
            }}

                >
                    <Typography variant='h3' padding='3px' textAlign='center'>Login</Typography>

                    {isSignup && (<TextField margin='normal' type={'text'} variant='outlined' placeholder='Name'/>)}

                    <TextField margin='normal' type={'email'} variant='outlined' placeholder='Email'/>

                    <TextField margin='normal' type={'password'} variant='outlined' placeholder='Password'/>
                    <Button sx={{marginTop:3, borderRadius:3}}
                    variant='contained' 
                    color='warning'>Login</Button>

                    <Button onClick={()=>setIsSignup(!isSignup)} sx={{marginTop:3, borderRadius:3}}>
                        Change to Signup Now
                    </Button>
                </Box> 
            </form>
        </div>
    )
}
export default Login;
